export class DefaultTool extends Controller implements ITool {

    start() {
        console.log('DefaultTool started');

        this.map.setInteractions([
            'DragPan',
            'MouseWheelZoom',
            'PinchZoom',
            'ZoomBox',
        ]);
    }

    stop() {
    }
}