import * as React from 'react';

import {UiComponent, UiState, UiProps} from './base';

interface ProgressProps extends UiProps {
    value: number;
    label?: string;
}

export class Progress extends UiComponent<ProgressProps, UiState> {
    render() {
        return <div className='uiProgressBar'>
            <div className='uiControlBox'>
                <div className='uiSmallbarOuter'>
                    <div className='uiSmallbarInner' style={{width: this.props.value + '%'}}/>
                </div>
            </div>
            {this.props.label && <div className="uiLabel">{this.props.label}</div>}
        </div>
    }
}

export class Loader extends UiComponent<UiProps, UiState> {
    render() {
        return <div className='uiLoader'/>;
    }
}

